SET FOREIGN_KEY_CHECKS=0;

-- ----------------------------
-- Table structure for `pt_blog`
-- ----------------------------
DROP TABLE IF EXISTS `pt_blog`;
CREATE TABLE `pt_blog` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `cid` int(11) NOT NULL COMMENT '分类id',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态 1.前台显示  2.前台不显示',
  `title` varchar(100) NOT NULL COMMENT '标题',
  `cover` varchar(100) NOT NULL COMMENT '封面',
  `intro` varchar(500) NOT NULL COMMENT '简要介绍',
  `content` text NOT NULL COMMENT '详细内容',
  `is_recommend` tinyint(1) NOT NULL DEFAULT '2' COMMENT '1.推荐  2.不推荐',
  `seo_title` varchar(100) NOT NULL COMMENT '网页标题',
  `seo_keyword` varchar(200) NOT NULL COMMENT '关键词',
  `seo_describe` text NOT NULL COMMENT '描述',
  `rank` tinyint(5) NOT NULL DEFAULT '0' COMMENT '排序',
  `ctime` int(11) NOT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`),
  KEY `cid` (`cid`) USING BTREE,
  KEY `rank` (`rank`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=12 DEFAULT CHARSET=utf8 COMMENT='博客表';

