-- Records of pt_blog
-- ----------------------------
INSERT INTO `pt_blog` VALUES ('3', '3', '1', 'linux服务器redis', '/upload/2018/0318/1521343820304.jpg', 'linux服务器下配置redis', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#FF0000;background-color:#ffffff;&quot;&gt;安装gcc&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;yum&amp;nbsp; install&amp;nbsp; gcc&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#FF0000;background-color:#ffffff;&quot;&gt;验证安装成功&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;rpm -qa |grep gcc&lt;/span&gt; \n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\1b1507a819b246b8950c102426ed8945\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;redis官网下载最近压缩包&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;a href=&quot;https://redis.io/download&quot;&gt;&lt;span style=&quot;color:#0000FF;&quot;&gt;https://redis.io/download&lt;/span&gt;&lt;/a&gt; \n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\2960cc0f04b14d6790bd1df35f4ebf8a\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Menlo;color:#333333;background-color:#fefefe;&quot;&gt;wget &lt;/span&gt;&lt;a href=&quot;http://download.redis.io/releases/redis-3.2.9.tar.gz&quot;&gt;&lt;span style=&quot;font-size:16px;font-family:Menlo;color:#0000FF;background-color:#FEFEFE;&quot;&gt;http://download.redis.io/releases/redis-3.2.9.tar.gz&lt;/span&gt;&lt;/a&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Menlo;color:#333333;background-color:#fefefe;&quot;&gt;tar xzf redis-3.2.9.tar.gz&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Menlo;color:#333333;background-color:#fefefe;&quot;&gt;cd redis-3.2.9&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Menlo;color:#333333;background-color:#fefefe;&quot;&gt;make&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-family:Microsoft YaHei,STXihei;color:#14191e;background-color:#ffffff;&quot;&gt;安装Redis：make PREFIX=/usr/local/redis install&lt;/span&gt; \n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\905d963f02b04ec88b1ecf1323066034\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\b68c5883228f4e3ca5027bff07511777\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-family:Microsoft YaHei,STXihei;color:#FF0000;background-color:#ffffff;&quot;&gt;将redis.conf拷贝到Redis安装目录&lt;/span&gt;&lt;span style=&quot;font-family:Microsoft YaHei,STXihei;color:#14191e;background-color:#ffffff;&quot;&gt;：cp redis.conf /usr/local/redis&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n cd /usr/local/redis/\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n vim redis.conf\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-family:Microsoft YaHei,STXihei;color:#14191e;background-color:#ffffff;&quot;&gt;daemonize no 改为 yes&lt;/span&gt; \n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\2933ca955f704de9919fe40b5d51e3ab\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\e08efea21bfc41d8ad87e7652ba0ae05\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n ./bin/redis-server ./redis.conf\n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\b9a8664850a741ae9b4132b94c858026\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;color:#FF0000;&quot;&gt;开启&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n ./bin/redis-cli\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;color:#FF0000;&quot;&gt;关闭&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n ./bin/redis-cli shutdown\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;color:#FF0000;&quot;&gt;查看进程&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n ps -ef | grep redis\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:18px;color:#FF0000;font-weight:bold;&quot;&gt;安装PHP扩展&lt;/span&gt; \n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\b305ef72fed640adb86e9c3e394edf5f\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n wget &lt;a href=&quot;https://github.com/phpredis/phpredis/archive/3.1.2.tar.gz&quot;&gt;&lt;span style=&quot;color:#0000FF;&quot;&gt;https://github.com/phpredis/phpredis/archive/3.1.2.tar.gz&lt;/span&gt;&lt;/a&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n tar zxvf 3.1.2.tar.gz\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n cd phpredis-3.1.2/\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n /usr/local/php/bin/phpize\n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\2e19b0edcf4b429d98e57d370b0a8835\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n ./configure --with-php-config=/usr/local/php/bin/php-config\n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\6ab5265ace4847a0b30a8e587c650ac1\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n make &amp;amp;&amp;amp; make install\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;color:#FF0000;&quot;&gt;成功&lt;/span&gt; \n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\10d68200d33a47c1bdfca444a5061091\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\dfc513b2336b4685a611ff280ff77c1f\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-family:Verdana;color:#333333;background-color:#ffffff;&quot;&gt;在最后一行添加以下内容&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-family:Verdana;color:#333333;background-color:#ffffff;font-weight:bold;&quot;&gt;extension=redis.so&lt;/span&gt; \n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\2ce84c031de34313a44499c280cb6f81\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;color:#FF0000;&quot;&gt;重启服务&lt;/span&gt; \n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\281f22148a5e40c38972679ed77f0adf\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>', '1', '', '', '', '0', '1496759643');
INSERT INTO `pt_blog` VALUES ('4', '3', '1', 'linux服务器下安装mysql', '/upload/2018/0318/1521343807789.jpg', 'linux服务器下安装mysql', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Arial;color:#FF0000;background-color:#f4ede3;&quot;&gt;下载地址&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Arial;color:#494949;background-color:#f4ede3;&quot;&gt;wget&amp;nbsp;&lt;/span&gt;&lt;a href=&quot;http://dev.mysql.com/get/Downloads/MySQL-5.6/mysql-5.6.33-linux-glibc2.5-x86_64.tar.gz&quot;&gt;&lt;span style=&quot;font-size:16px;font-family:Arial;color:#0000FF;background-color:#F4EDE3;&quot;&gt;http://dev.mysql.com/get/Downloads/MySQL-5.6/mysql-5.6.33-linux-glibc2.5-x86_64.tar.gz&lt;/span&gt;&lt;/a&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Consolas;color:#008200;background-color:#f4f4f4;&quot;&gt;#解压&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Consolas;color:#ff1493;&quot;&gt;tar&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Consolas;color:#494949;background-color:#ffffff;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Consolas;&quot;&gt;-zxvf mysql-5.6.33-linux-glibc2.5-x86_64.&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Consolas;color:#ff1493;&quot;&gt;tar&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Consolas;&quot;&gt;.gz&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Consolas;color:#008200;background-color:#f4f4f4;&quot;&gt;#复制解压后的mysql目录&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Consolas;color:#ff1493;&quot;&gt;cp&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Consolas;color:#494949;background-color:#ffffff;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Consolas;&quot;&gt;-r mysql-5.6.33-linux-glibc2.5-x86_64&amp;nbsp;/usr/local/mysql&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Consolas;color:#008200;background-color:#f4f4f4;&quot;&gt;#添加用户组&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Consolas;background-color:#ffffff;&quot;&gt;groupadd mysql&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Consolas;color:#008200;background-color:#f4f4f4;&quot;&gt;#添加用户mysql 到用户组mysql&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Consolas;color:#ff1493;background-color:#ffffff;&quot;&gt;useradd&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Consolas;color:#494949;background-color:#ffffff;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Consolas;background-color:#ffffff;&quot;&gt;-g mysql mysql&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Consolas;color:#ff1493;&quot;&gt;cd&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Consolas;color:#494949;background-color:#f4f4f4;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Consolas;&quot;&gt;/usr/local/mysql/&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#FF0000;background-color:#ffffff;&quot;&gt;修改当前目录拥有者为mysql用户&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;chown -R mysql:mysql ./&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;安装数据库：&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;./scripts/mysql_install_db --user=mysql&lt;/span&gt; \n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\36de2633b9074c5bb292cac85e956043\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;color:#FF0000;&quot;&gt;当出现报错&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n yum install -y perl-Data-Dumper\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Verdana;color:#393939;background-color:#faf7ef;font-weight:bold;&quot;&gt;&amp;nbsp; &amp;nbsp; 8、修改当前目录拥有者为root用户&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Courier New;color:#008080;background-color:#f5f5f5;&quot;&gt;chown&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;background-color:#f5f5f5;&quot;&gt; -R root:root ./&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Verdana;color:#393939;background-color:#faf7ef;font-weight:bold;&quot;&gt;&amp;nbsp; &amp;nbsp; 9、修改当前data目录拥有者为mysql用户&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Courier New;color:#008080;background-color:#f5f5f5;&quot;&gt;chown&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;background-color:#f5f5f5;&quot;&gt; -R &lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;color:#008080;background-color:#f5f5f5;&quot;&gt;mysql&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;background-color:#f5f5f5;&quot;&gt;:&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;color:#008080;background-color:#f5f5f5;&quot;&gt;mysql&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;background-color:#f5f5f5;&quot;&gt; data&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Verdana;color:#393939;background-color:#faf7ef;&quot;&gt;添加开机启动，把启动脚本放到开机初始化目录。&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Courier New;background-color:#f5f5f5;&quot;&gt;cp support-files/&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;color:#008080;background-color:#f5f5f5;&quot;&gt;mysql&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;background-color:#f5f5f5;&quot;&gt;.server /etc/init.d/&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;color:#008080;background-color:#f5f5f5;&quot;&gt;mysql&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Courier New;&quot;&gt;#创建缺少的文件夹&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Courier New;&quot;&gt;mkdir /var/log/mariadb&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Courier New;font-style:italic;&quot;&gt;service &lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;color:#008080;font-style:italic;&quot;&gt;mysql&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;font-style:italic;&quot;&gt; start&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-family:Verdana;background-color:#faf7ef;font-weight:bold;&quot;&gt;正常提示信息：&lt;/span&gt;&lt;span style=&quot;font-family:Verdana;color:#339966;background-color:#faf7ef;font-weight:bold;&quot;&gt;Starting MySQL. SUCCESS!&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-family:Verdana;color:#393939;background-color:#faf7ef;font-weight:bold;&quot;&gt;12、把mysql客户端放到默认路径&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Courier New;background-color:#f5f5f5;&quot;&gt;ln -s /usr/local/&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;color:#008080;background-color:#f5f5f5;&quot;&gt;mysql&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;background-color:#f5f5f5;&quot;&gt;/bin/&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;color:#008080;background-color:#f5f5f5;&quot;&gt;mysql&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;background-color:#f5f5f5;&quot;&gt; /usr/local/bin/&lt;/span&gt;&lt;span style=&quot;font-size:16px;font-family:Courier New;color:#008080;background-color:#f5f5f5;&quot;&gt;mysql&lt;/span&gt; \n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\eeb7dd02397a4e0fa7609138389ad1ce\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\947674c37aee4afbbb5b7cbfd28b2493\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div&gt;\n &lt;img src=&quot;C:\\Users\\Administrator\\AppData\\Local\\YNote\\data\\qqA07A4A247E86C94A9133D1972EC93EB4\\7af60a8169fb40db9b1b7a2202bc8380\\clipboard.png&quot; /&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Verdana;color:#393939;background-color:#faf7ef;&quot;&gt;ln -s /var/lib/mysql/mysql.sock /tmp/mysql.sock&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Verdana;background-color:#faf7ef;&quot;&gt;通过使用 mysql -uroot -p 连接数据库（默认数据库的root用户没有密码，这个需要设置一个密码）。&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Verdana;color:#FF0000;background-color:#faf7ef;&quot;&gt;完成&lt;/span&gt; \n&lt;/div&gt; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>', '1', '', '', '', '0', '1496759674');
INSERT INTO `pt_blog` VALUES ('5', '3', '1', 'mysql配置远程连接', '/upload/2018/0318/1521343800452.jpg', 'mysql配置远程连接', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;使用“mysql -uroot -proot”命令可以连接到本地的mysql服务。&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;使用“use mysql”命令，选择要使用的数据库，修改远程连接的基本信息，&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;保存在mysql数据库中，因此使用mysql数据库。&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;使用“GRANT ALL PRIVILEGES ON *.* TO &#39;root&#39;@&#39;%&#39; IDENTIFIED BY &#39;root&#39; WITH GRANT OPTION;”&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;命令可以更改远程连接的设置。&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;使用“flush privileges;”命令刷新刚才修改的权限，使其生效。&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;select host,user from user&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-family:Verdana;background-color:#ffffff;&quot;&gt;mysql -uroot -p&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-family:Verdana;background-color:#ffffff;&quot;&gt;（密码）&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-family:Verdana;background-color:#ffffff;&quot;&gt;GRANT ALL PRIVILEGES ON *.* TO &#39;laysen&#39;@&#39;%&#39; IDENTIFIED BY &#39;123456&#39; WITH GRANT OPTION;&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-family:Verdana;background-color:#ffffff;&quot;&gt;（第一个itoffice表示用户名，%表示所有的电脑都可以连接，也可以设置某个ip地址运行连接，第二个itoffice表示密码）。&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-family:Verdana;background-color:#ffffff;&quot;&gt;flush privileges;&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-family:Verdana;background-color:#ffffff;&quot;&gt;命令立即生效&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-family:Verdana;background-color:#ffffff;&quot;&gt;SELECT DISTINCT CONCAT(&#39;User: &#39;&#39;&#39;,user,&#39;&#39;&#39;@&#39;&#39;&#39;,host,&#39;&#39;&#39;;&#39;) AS query FROM mysql.user;&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-family:Verdana;background-color:#ffffff;&quot;&gt;查询数据库的用户（看到如下内容表示创建新用户成功了）&lt;/span&gt; \n&lt;/div&gt; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>', '1', '', '', '', '0', '1496759716');
INSERT INTO `pt_blog` VALUES ('6', '3', '1', '微信验证获取token', '/upload/2018/0318/1521343791886.jpg', '微信验证获取token', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div&gt; &lt;span style=&quot;font-size:14px;line-height:1.5;&quot;&gt;&amp;lt;?php&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n //php验证微信token\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n define(&quot;TOKEN&quot;, &quot;laysen&quot;);\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $wechatObj = new wechatCallbackapiTest();\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $wechatObj-&amp;gt;valid();\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n class wechatCallbackapiTest{\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n public function valid(){\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $echoStr = $_GET[&quot;echostr&quot;];\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n if($this-&amp;gt;checkSignature()){\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n //验证\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n echo $echoStr;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n exit;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n private function checkSignature(){\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $signature = $_GET[&quot;signature&quot;];\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $timestamp = $_GET[&quot;timestamp&quot;];\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $nonce = $_GET[&quot;nonce&quot;];\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $token = TOKEN;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $tmpArr = array($token, $timestamp, $nonce);\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n sort($tmpArr);//排序\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $tmpStr = implode( $tmpArr );\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $tmpStr = sha1( $tmpStr );//加密\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n if( $tmpStr == $signature ){\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n return true;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }else{\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n return false;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n ?&amp;gt;\n&lt;/div&gt; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>', '1', '', '', '', '0', '1496759764');
INSERT INTO `pt_blog` VALUES ('7', '3', '1', 'php文件静态缓存', '/upload/2018/0318/1521343781459.jpg', 'php文件静态缓存', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:18px;color:#FF0000;font-weight:bold;&quot;&gt;引入第三方类库&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:18px;color:#FF0000;&quot;&gt;存入&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $arr = array(&#39;1&#39;,&#39;2&#39;,&#39;3&#39;);\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;color:#FF0000;&quot;&gt;第三个参数，缓存时间&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $res = $files-&amp;gt;cacheDate(&#39;laysen&#39;,$arr,20);\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:18px;color:#FF0000;&quot;&gt;获取&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $res = $files-&amp;gt;cacheDate(&#39;laysen&#39;);\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:18px;color:#FF0000;&quot;&gt;删除&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $res = $files-&amp;gt;cacheDate(&#39;laysen&#39;,null);\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:18px;color:#FF0000;&quot;&gt;类库&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &amp;lt;?php\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n namespace Org\\Laysen;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n class FileDate{\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n private $_dir;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n const EXT = &#39;.txt&#39;;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n public function __construct(){\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n // 缓存目录\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $this-&amp;gt;_dir = dirname(__FILE__) . &#39;/files/&#39;;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n // 文件缓存\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n public function cacheDate($key , $value = &#39;&#39;, $cacheTime = 0){\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n //缓存文件名\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $filename = $this-&amp;gt;_dir . $key . self::EXT;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n if($value !== &#39;&#39;){\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n // $value为null时，删除缓存文件\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n if(is_null($value)){\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n return @unlink&#40;$filename&#41;;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n // 创建目录\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $dir = dirname($filename);\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n if(!is_dir($dir)){\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n mkdir($dir , 0777);\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n // 生成缓存时间\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $cacheTime = sprintf(&#39;1d&#39; , $cacheTime);\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n // 创建缓存文件\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n if(file_put_contents($filename,$cacheTime . json_encode($value))){\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n return true;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n return false;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n if(!is_file&#40;$filename&#41;){\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n return false;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n // 读取缓存文件\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $contents = file_get_contents&#40;$filename&#41;;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $cacheTime = (int)substr($contents, 0 , 11);\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n // 判断缓存文件是否过期\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n if($cacheTime != 0 &amp;amp;&amp;amp; ($cacheTime + filemtime($filename)) &amp;lt; time()){\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n unlink&#40;$filename&#41;;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n return false;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $value = substr($contents,11);\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n return json_decode($value);\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>', '1', '', '', '', '0', '1496759817');
INSERT INTO `pt_blog` VALUES ('8', '3', '1', 'mongdb配置服务', '/upload/2018/0318/1521343771423.jpg', 'mongdb配置服务', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:22px;color:#FF0000;&quot;&gt;#配置服务文件&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:22px;color:#FF0000;&quot;&gt;mongodb.config&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;##数据文件&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;dbpath=D:\\mongodb\\data\\db&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;##日志文件&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;logpath=D:\\mongodb\\log\\mongodb.log&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;##错误日志采用追加模式，配置这个选项后mongodb的日志会追加到现有的日志文件，而不是从新创建一个新文件&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;logappend=true &lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;#启用日志文件，默认启用&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;journal=true &lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;#这个选项可以过滤掉一些无用的日志信息，若需要调试使用请设置为false&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;quiet=true &lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;#端口号 默认为27017&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Microsoft YaHei,STXihei;color:#333333;background-color:#ffffff;&quot;&gt;port=27017 &lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;color:#FF0000;&quot;&gt;开启mongdb服务&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n mongod --config D:\\mongodb\\mongodb.config\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;color:#FF0000;&quot;&gt;安装服务&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n mongod --config D:\\mongodb\\mongodb.config --install\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;color:#FF0000;&quot;&gt;删除服务&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n mongodb --remove --serviceName &quot;MongoDB&quot;\n&lt;/div&gt; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>', '1', '', '', '', '0', '1496759847');
INSERT INTO `pt_blog` VALUES ('9', '3', '1', '常用正则表达式', '/upload/2018/0318/1521343838428.jpg', '常用正则表达式', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&lt;div style=&quot;font-size:14px;&quot;&gt;\n if(!reg.test(amount)){}\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n 判断金额格式\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n /^[0-9]*(\\.[0-9]{1,2})?$/\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n 判断中文\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n var reg = /[0-9\\u4e00-\\u9fa5]$/;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n 正整数\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n var reg = /^\\+?[1-9][0-9]*$/;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n var reg = /^0?(13[0-9]|15[0-9]|17[0-9]|18[0-9]|14[0-9])[0-9]{8}$/;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n if (!reg.test(phone)) {\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n Alert&#40;&quot;手机号码格式不正确&quot;&#41;;return false;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n var reg = &nbsp;/^(\\d*|-*|\\s)*$/;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n if (!reg.test(phone)) {\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n Alert&#40;&quot;电话号码格式不正确&quot;&#41;;return false;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n }\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n 身份证号\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n /^(\\d{6})(\\d{4})(\\d{2})(\\d{2})(\\d{3})([0-9]|X)$/\n&lt;/div&gt; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>', '1', '', '', '', '3', '1496759876');
INSERT INTO `pt_blog` VALUES ('10', '3', '1', 'linux服务器安装svn', '/upload/2018/0318/1521343829882.jpg', 'linux服务器安装svn', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;a href=&quot;http://blog.163.com/longsu2010@yeah/blog/static/173612348201202114212933/&quot;&gt;&lt;span style=&quot;color:#0000FF;&quot;&gt;http://blog.163.com/longsu2010@yeah/blog/static/173612348201202114212933/&lt;/span&gt;&lt;/a&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;1、yum install subversion&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;2、输入rpm -ql subversion查看安装位置&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;3、创建svn版本库目录&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;mkdir -p /var/svn/svnrepos&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;4、创建版本库&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;svnadmin create /var/svn/svnrepos&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;5、进入conf目录（该svn版本库配置文件）&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;authz文件是权限控制文件&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;passwd是帐号密码文件&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;svnserve.conf SVN服务配置文件&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;6、设置帐号密码&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;vi passwd&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;在[users]块中添加用户和密码，格式：帐号=密码，如dan=dan&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;7、设置权限&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;vi authz&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;在末尾添加如下代码：&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;[/]&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;dan=rw&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;w=r&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;意思是版本库的根目录dan对其有读写权限，w只有读权限。&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;8、修改svnserve.conf文件&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;vi svnserve.conf&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;打开下面的几个注释：&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;anon-access = read #匿名用户可读&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;auth-access = write #授权用户可写&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;password-db = passwd #使用哪个文件作为账号文件&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;authz-db = authz #使用哪个文件作为权限文件&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;realm = /var/svn/svnrepos # 认证空间名，版本库所在目录&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;9、启动svn版本库&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;svnserve -d -r /var/svn/svnrepos&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;设置钩子&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n svn checkout svn://localhost /yjdata/\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n vim post-commit\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n #!/bin/sh\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n SVN=/usr/bin/svn\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n WEB=/data/wwwroot\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n $SVN update $WEB --username laysen --password 123456\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n chmod 777 post-commit\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n 杀掉进程 重启版本\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:15px;font-family:Verdana;color:#FF0000;background-color:#fefef2;font-weight:bold;&quot;&gt;杀死进程&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;ps -ef|grep svnserve&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;kill 28914&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;br /&gt;\n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;解锁&lt;/span&gt; \n&lt;/div&gt;\n&lt;div style=&quot;font-size:14px;&quot;&gt;\n &lt;span style=&quot;font-size:16px;font-family:Hiragino Sans GB W3;color:#595959;background-color:#fafafa;&quot;&gt;svn cleanup&lt;/span&gt; \n&lt;/div&gt; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>', '1', '', '', '', '1', '1496759933');

-- ----------------------------
-- Table structure for `pt_blog_cate`
-- ----------------------------
DROP TABLE IF EXISTS `pt_blog_cate`;
CREATE TABLE `pt_blog_cate` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(100) NOT NULL COMMENT '分类名称',
  `rank` tinyint(5) NOT NULL DEFAULT '0' COMMENT '排序',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态 1.前台显示  2.前台不显示',
  `ctime` int(10) NOT NULL DEFAULT '0' COMMENT '创建时间',
  PRIMARY KEY (`id`),
  KEY `rank` (`rank`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='博客分类表';

-- ----------------------------
-- Records of pt_blog_cate
-- ----------------------------
INSERT INTO `pt_blog_cate` VALUES ('2', '感情', '6', '1', '1496755800');
INSERT INTO `pt_blog_cate` VALUES ('3', '网站建设', '5', '1', '1496757480');

-- ----------------------------
-- Table structure for `pt_blog_message`
-- ----------------------------
DROP TABLE IF EXISTS `pt_blog_message`;
CREATE TABLE `pt_blog_message` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(100) NOT NULL COMMENT '留言姓名',
  `phone` varchar(100) NOT NULL COMMENT '留言手机号',
  `email` varchar(100) NOT NULL COMMENT '留言人邮箱',
  `content` text NOT NULL COMMENT '留言内容',
  `IP` varchar(20) NOT NULL COMMENT 'IP地址',
  `status` tinyint(1) NOT NULL DEFAULT '2' COMMENT '状态 1.已审核  2.未审核',
  `rank` tinyint(5) NOT NULL DEFAULT '100' COMMENT '排序',
  `ctime` int(11) NOT NULL COMMENT '留言时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='博客留言表';

-- ----------------------------
-- Records of pt_blog_message
-- ----------------------------
